/**
 * Generates a random hex string
 * 
 * @param bytes The number of bytes to generate (The string will be twice as long)
 */
declare function _nirvati_getRandomValues(bytes: number): string;

/**
 * Logs a message to the console
 * 
 * @param msg The message to log
 */
declare function _nirvati_dbg(msg: unknown): void;
